%4.3function [L, U] = LUdecompCrount(A)%A função decompõe a matriz A em uma matriz triangular inferior L e%em uma matriz triangular superior U, usando o método de Crout de forma que A = LU.%Variáveis de entrada:%A Matriz de coeficientes.%b vetor coluna de constantes.%Variáveis de saída:%L Matriz triangular inferior.%U Matriz triangular superior.[R, C] = size(A);L = zeros(R, C);U = zeros(R, C);for i = 1 :R                                                     %-------    L(i, 1) = A(i, 1);  %Eq.(4.34)                                       | Passo 1 e 2 (p.136)    U(i,i) = 1;         %Eq.(4.35)                                       |end                                                              %-------for j = 2: R                                                     %-------    U(1, j) = A(1, j)/L(1, 1); %Eq.(4.36)                                |  Passo 3 (p.136)end                                                              %-------for i = 2:R                                                      %-------    for j = 2:i                                                  %        |        L(i, j) = A(i, j) - L(i, 1: j-1)*U(1:j-1,j); %Eq.(4.37)           |    end                                                          %        |    for j = i+1 : R                                              %          Passo 4 (p.136)        U(i,j) = (A(i,j) - L(i, 1: i-1)*U(1:i-1, j))/ L(i,i); %Eq.(4.38)  |     end                                                          %        |end                                                              %--------end